svg.script:
    function menu(evt) {

        var trigger = document.getElementById("searchtransform");

        if(typeof clicked === 'undefined') {

            clicked = true;

            trigger.setAttribute("from", "0 240 330");
            trigger.setAttribute("to", "45 240 330");

        } else {

            delete clicked;

            trigger.setAttribute("from", "45 240 330");
            trigger.setAttribute("to", "0 240 330");
        }
    }
